    function addRemove() {
        return {
            fields: [],
            addNewField() {
                if (this.fields.length >= 10) {
                    return alert('Only 10 can be created.');
                }
                this.fields.push({id: new Date().getTime() + this.fields.length});
            },
            removeField(field) {
                this.fields.splice(this.fields.indexOf(field), 1);
            }
        }
    }
